import randomimport timeimport threadingprint('Maths game ready ')def game4():    points=0    print("RULES:")    print("---------------------------------------------------------")    print("Every correct answer gives you one point")    print("Everytime you exceed the time, your one point gets deducted")    print("Wrong answers take away 2 points")    print("wrong answers which exceed the time cause a 3 point deduction")    print("---------------------------------------------------------")    print()    tries=int(input('You would like to take up the challenge for how many questions?? (Any number > 0)'))    timesss=int(input('How many seconds would you need to solve a question??'))    def time_expired():        print("No ")        global fail        fail = True            for i in range (tries):        num2=random.randrange(1, 1000, 1)        num1=random.randrange(1, 1000, 1)        operation=random.choice(['add','subtract'])        print(operation,' ',num1,' and ',' ',num2)        print('ten seconds to think')        fail = False        time = threading.Timer(timesss, time_expired)        time.start()        print("You have ",timesss," seconds to choose the correct answer.\n")        prompt = int(input(">>>"))        if prompt != None and not fail:            print("Point")            time.cancel()        if fail==True:            points-=1        if operation=='add' and num1+num2 == prompt:            print('correct\n')            points+=1        elif operation=='add' and num1+num2 != prompt:            print('Wrong\n',num1+num2)            points-=2        elif operation=='subtract' and num1-num2 == prompt:            print('correct\n')            points+=1        elif operation=='subtract' and num1-num2 != prompt:            print('Wrong\n',num1-num2)            points-=2    print(points)